import React, { useState } from 'react';
import './Notice.css';

const Notice = () => {
  // 공지사항 상태 관리
  const [notices, setNotices] = useState([
    { id: 1, title: '환영합니다!', content: '우리 서비스를 이용해 주셔서 감사합니다!', image: '', video: '' },
    { id: 2, title: '유지보수 안내', content: '9월 1일에 서비스 유지보수가 있을 예정입니다.', image: '', video: '' }
  ]);
  const [newNotice, setNewNotice] = useState({ title: '', content: '', image: '', video: '' });

  // 텍스트 스타일 상태
  const [textStyle, setTextStyle] = useState({
    fontSize: '1rem',
    fontWeight: 'normal',
    color: '#333'
  });

  // 입력값 변경 처리
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNotice({ ...newNotice, [name]: value });
  };

  // 이미지 파일 변경 처리
  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (files.length > 0) {
      const fileUrl = URL.createObjectURL(files[0]);
      setNewNotice({ ...newNotice, [name]: fileUrl });
    }
  };

  // 공지사항 추가 처리
  const addNotice = (e) => {
    e.preventDefault();
    if (newNotice.title && newNotice.content) {
      setNotices([
        ...notices,
        { id: notices.length + 1, title: newNotice.title, content: newNotice.content, image: newNotice.image, video: newNotice.video }
      ]);
      setNewNotice({ title: '', content: '', image: '', video: '' });
    }
  };

  // 공지사항 삭제 처리
  const deleteNotice = (id) => {
    setNotices(notices.filter((notice) => notice.id !== id));
  };

  // 텍스트 스타일 적용 핸들러
  const applyTextStyle = () => {
    const preview = document.querySelector('.notice-preview');
    if (preview) {
      preview.style.fontSize = textStyle.fontSize;
      preview.style.fontWeight = textStyle.fontWeight;
      preview.style.color = textStyle.color;
    }
  };

  return (
    <div className="container">
      <header className="main-header">
        <h1>공지사항 관리</h1>
      </header>
      <div className="notice-section">
        <section className="text-style-controls">
          <label>
            글씨 크기:
            <select value={textStyle.fontSize} onChange={(e) => setTextStyle({ ...textStyle, fontSize: e.target.value })}>
              <option value="0.8rem">작게</option>
              <option value="1rem">보통</option>
              <option value="1.2rem">크게</option>
              <option value="1.5rem">아주 크게</option>
            </select>
          </label>
          <label>
            글씨 굵기:
            <select value={textStyle.fontWeight} onChange={(e) => setTextStyle({ ...textStyle, fontWeight: e.target.value })}>
              <option value="normal">보통</option>
              <option value="bold">굵게</option>
            </select>
          </label>
          <label>
            글씨 색상:
            <input
              type="color"
              value={textStyle.color}
              onChange={(e) => setTextStyle({ ...textStyle, color: e.target.value })}
            />
          </label>
          <button onClick={applyTextStyle}>적용</button>
        </section>
        <section className="notice-preview">
          <h4>미리보기</h4>
          <p>{newNotice.content || '공지사항 내용이 여기에 표시됩니다.'}</p>
        </section>
        <section className="notice-form">
          <h4>새 공지사항 추가</h4>
          <form onSubmit={addNotice}>
            <input
              type="text"
              name="title"
              placeholder="공지사항 제목"
              value={newNotice.title}
              onChange={handleInputChange}
              required
            />
            <textarea
              name="content"
              placeholder="공지사항 내용"
              value={newNotice.content}
              onChange={handleInputChange}
              required
            ></textarea>
            <input
              type="file"
              name="image"
              accept="image/*"
              onChange={handleFileChange}
            />
            <input
              type="file"
              name="video"
              accept="video/*"
              onChange={handleFileChange}
            />
            <button type="submit">공지사항 등록</button>
          </form>
        </section>
        <section className="notice-list">
          <h4>공지사항 목록</h4>
          <ul>
            {notices.map((notice) => (
              <li key={notice.id}>
                <h5>{notice.title}</h5>
                <p>{notice.content}</p>
                {notice.image && (
                  <div className="notice-media">
                    <img src={notice.image} alt="공지사항 이미지" />
                  </div>
                )}
                {notice.video && (
                  <div className="notice-media">
                    <video controls>
                      <source src={notice.video} type="video/mp4" />
                      Your browser does not support the video tag.
                    </video>
                  </div>
                )}
                <button onClick={() => deleteNotice(notice.id)}>삭제</button>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
};

export default Notice;
