import React, { useState } from 'react';
import './Notice.css';

const Notice = () => {
  // 공지사항 상태 관리
  const [notices, setNotices] = useState([
    { id: 1, title: '환영합니다!', content: '우리 서비스를 이용해 주셔서 감사합니다!', image: '', video: '' },
    { id: 2, title: '유지보수 안내', content: '9월 1일에 서비스 유지보수가 있을 예정입니다.', image: '', video: '' }
  ]);
  const [newNotice, setNewNotice] = useState({ title: '', content: '', image: '', video: '' });
  const [style, setStyle] = useState({ fontSize: '1rem', fontWeight: 'normal', color: '#333' });

  // 입력값 변경 처리
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNotice({ ...newNotice, [name]: value });
  };

  // 이미지 파일 변경 처리
  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (files.length > 0) {
      const fileUrl = URL.createObjectURL(files[0]);
      setNewNotice({ ...newNotice, [name]: fileUrl });
    }
  };

  // 공지사항 추가 처리
  const addNotice = (e) => {
    e.preventDefault();
    if (newNotice.title && newNotice.content) {
      setNotices([
        ...notices,
        { id: notices.length + 1, ...newNotice }
      ]);
      setNewNotice({ title: '', content: '', image: '', video: '' });
    }
  };

  // 스타일 변경 처리
  const handleStyleChange = (e) => {
    const { name, value } = e.target;
    setStyle({ ...style, [name]: value });
  };

  return (
    <div className="container">
      <header className="main-header">
        <h1>공지사항</h1>
      </header>
      <section className="notice-form">
        <h4>새 공지사항 작성</h4>
        <form onSubmit={addNotice}>
          <input
            type="text"
            name="title"
            placeholder="제목"
            value={newNotice.title}
            onChange={handleInputChange}
            required
          />
          <textarea
            name="content"
            placeholder="내용"
            value={newNotice.content}
            onChange={handleInputChange}
            required
          />
          <input
            type="file"
            name="image"
            accept="image/*"
            onChange={handleFileChange}
          />
          <input
            type="file"
            name="video"
            accept="video/*"
            onChange={handleFileChange}
          />
          <button type="submit">공지사항 추가</button>
        </form>
      </section>
      <section className="text-style-controls">
        <label>
          글씨 크기:
          <select name="fontSize" value={style.fontSize} onChange={handleStyleChange}>
            <option value="1rem">작음</option>
            <option value="1.2rem">보통</option>
            <option value="1.4rem">크게</option>
          </select>
        </label>
        <label>
          굵기:
          <select name="fontWeight" value={style.fontWeight} onChange={handleStyleChange}>
            <option value="normal">보통</option>
            <option value="bold">굵게</option>
          </select>
        </label>
        <label>
          색상:
          <input
            type="color"
            name="color"
            value={style.color}
            onChange={handleStyleChange}
          />
        </label>
        <button onClick={() => setStyle({ ...style })}>적용</button>
      </section>
      <section className="notice-preview" style={style}>
        <h4>미리보기</h4>
        <p>스타일 적용된 텍스트 표시</p>
      </section>
      <section className="notice-list">
        <h4>공지사항 목록</h4>
        <ul>
          {notices.map(notice => (
            <li key={notice.id}>
              {notice.image && <img src={notice.image} alt="공지사항 이미지" />}
              {notice.video && <video src={notice.video} controls />}
              <h5>{notice.title}</h5>
              <p>{notice.content}</p>
              <button>자세히 보기</button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default Notice;
