// front/src/Views/subfolder/admin/index.js

import React, { useState, useEffect } from 'react';
import Chart from 'chart.js/auto'; // Chart.js의 최신 버전 import
import './style.css';

const Admin = () => {
  const [data, setData] = useState({ active: 0, inactive: 0 });

  useEffect(() => {
    // 데이터 요청 및 차트 업데이트
    const fetchData = async () => {
      try {
        const response = await fetch('/api/member-stats');
        const result = await response.json();
        setData({ active: result.active, inactive: result.inactive });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    // 주기적으로 데이터 업데이트
    const intervalId = setInterval(fetchData, 24 * 60 * 60 * 1000); // 1일마다

    // 컴포넌트 언마운트 시 인터벌 클리어
    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const ctx = document.getElementById('memberChart').getContext('2d');
    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Active Members', 'Inactive Members'],
        datasets: [{
          label: 'Members',
          data: [data.active, data.inactive],
          backgroundColor: ['#36A2EB', '#FF6384'],
          borderColor: ['#36A2EB', '#FF6384'],
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          tooltip: {
            callbacks: {
              label: function(context) {
                let label = context.dataset.label || '';
                if (label) {
                  label += ': ';
                }
                if (context.parsed.y !== null) {
                  label += new Intl.NumberFormat().format(context.parsed.y);
                }
                return label;
              }
            }
          }
        },
        scales: {
          x: {
            beginAtZero: true,
            grid: {
              display: false
            }
          },
          y: {
            beginAtZero: true,
            grid: {
              borderDash: [5, 5]
            }
          }
        }
      }
    });
  }, [data]); // 데이터가 변경될 때마다 차트 업데이트

  return (
    <div className="container">
      <h2>Member Statistics</h2>
      <canvas id="memberChart"></canvas>
    </div>
  );
};

export default Admin;
