              </Link>
            </div>
  
            <div className="search-container">
              <form onSubmit={handleSearch}>
                <input
                  type="text"
                  placeholder="갤럭시 Z플립5"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <Link to="/">
                  <img
                    src={Searchlogo}
                    alt="Search"
                    className="Search-button"
                  />
                </Link>
              </form>
            </div>
  
            <div className="user-actions">
              <Link to="/cart">
                <img src={cartLogo} alt="cart" className="cart-button" />
              </Link>
              <Link to="/login">
                <img src={loginlogo} alt="login" className="login-button" />
              </Link>
            </div>
          </div>
  
          {/*앞쪽메뉴들 */}
          <ul className="navbar">
            {menus.map((menu, index) => (
              <HeaderCard
              key={index}
              title={menu.title}
              links={menu.links}
              isActive={activeMenu === index}
              setActiveMenu={() => setActiveMenu(index)}
              className="navbar-item"
              onMouseEnter={handleMenuHover} // 메뉴에 호버했을 때 이벤트 핸들러 호출
              onMouseLeave={handleMenuLeave} // 호버를 떠났을 때 이벤트 핸들러 호출
            />
            
            ))}
  
            {/* 뒤쪽메뉴들 */}
            <div className="spacer" />
            {additionalMenus.map((menu, index) => (
              <HeaderCard
                key={index + menus.length}
                title={menu.title}
                links={menu.links}
                isActive={activeMenu === index + menus.length}
                setActiveMenu={() => setActiveMenu(index + menus.length)}
                className="navbar-item"
                onMouseEnter={handleMenuHover}
                onMouseLeave={handleMenuLeave}
              />
            ))}
          </ul>
        </nav>
      </header>
    );
  };
  
  export default Header;
