import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './style.css';

const Register = () => {
    const [message, setMessage] = useState(''); 

    const handleSubmit = (e) => {
        e.preventDefault(); 
        setMessage('회원가입이 완료되었습니다.'); 
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2 style={{ textAlign: 'center', color: '#0f4c75' }}>회원가입</h2>

                <form onSubmit={handleSubmit}>
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" placeholder="이메일을 입력하세요." required />

                    <label htmlFor="username">Username</label>
                    <input type="text" id="username" placeholder="아이디를 입력하세요." required />

                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" placeholder="비밀번호를 입력하세요." required />

                    <label htmlFor="confirmPassword">Confirm Password</label>
                    <input type="password" id="confirmPassword" placeholder="입력한 비밀번호를 확인하세요." required />


                    <label htmlFor="termsAndConditions">약관 동의</label>
                    <textarea id="termsAndConditions" style={{width: '380px', height: '50px'}} readOnly>
                    여러분을 환영합니다.
                        네이버 서비스 및 제품(이하 ‘서비스’)을 이용해 주셔서 감사합니다. 본 약관은 다양한 네이버 서비스의 이용과 관련하여 네이버 서비스를 제공하는 네이버 주식회사(이하 ‘네이버’)와 이를 이용하는 네이버 서비스 회원(이하 ‘회원’) 또는 비회원과의 관계를 설명하며, 아울러 여러분의 네이버 서비스 이용에 도움이 될 수 있는 유익한 정보를 포함하고 있습니다.

                        네이버 서비스를 이용하시거나 네이버 서비스 회원으로 가입하실 경우 여러분은 본 약관 및 관련 운영 정책을 확인하거나 동의하게 되므로, 잠시 시간을 내시어 주의 깊게 살펴봐 주시기 바랍니다.
                    </textarea>
                    <br/>
                    <div className="agreement">
                        <input type="checkbox" id="agree" required />
                        <label htmlFor="agree">동의</label>
                    </div>


                    <button type="submit">회원가입</button>
                </form>
            </div>

            {message && (
                <div className="message-box">
                    <p>{message}</p>
                    <Link to="/login" className="login-link">로그인하러 가기</Link>
                </div>
            )}
        </div>
    );
};

export default Register;
