import React, { useState, useEffect } from 'react';
import Slideshow from '../Slideshow/Slideshow';
import './style.css';
import banner1 from '../../images/banner1.png';
import banner2 from '../../images/banner2.png';
import banner3 from '../../images/banner3.png';
import banner4 from '../../images/banner4.png';
import subimg1 from '../../images/subimg1.png';
import subimg2 from '../../images/subimg2.png';
import subimg3 from '../../images/subimg3.png';
import subimg4 from '../../images/subimg4.png';
import subimg5 from '../../images/subimg5.png';
import subimg6 from '../../images/subimg6.png';
import subimg7 from '../../images/subimg7.png';
import subimg8 from '../../images/subimg8.png';
import bigimg1 from '../../images/bigimg1.png';
import bigimg2 from '../../images/bigimg2.png';

const Home = () => {
  const images = [
    {
      url: banner3,
      title: "Galaxy Z Fold5 | Z Filp5 | \n S23 Series",
      titleStyle: {color: 'black', textAlign: 'left'},
      subtitle: "Z 폴드51TB 그레이 18% 즉시할인! \n 갤럭시로 따뜻한 연말 선물 미리 준비하세요 \n \n 2023.12.01.~2023.12.31.",
      subtitleStyle: { color: 'black', whiteSpace:'pre-line', 
      textAlign: 'left', top: "53%" }
    },
    {
      url: banner4,
      title: "BESPOKE 그랑데 AI \n 연말 베스트 어워즈",
      titleStyle: {color: 'black', textAlign: 'left'},
      subtitle: "올해 가장 사랑받은 BESPOKE 그랑데 AI \n 최대 43% 할인과 함께 만나보세요! \n \n 2023.12.01.~2023.12.31.", 
      subtitleStyle: { color: 'black', whiteSpace: 'pre-line', textAlign: 'left', top: "53%"}
    },
    {
      url: banner1,
      title: "삼성닷컴 월간TV",
      titleStyle: {color: 'white'},
      subtitle: "삼케플 할인받고 더 오래 더 안전하게! \n 대형TV, 망설이지 말고 지금 구매하세요",
      subtitleStyle: { color: 'white', whiteSpace:'pre-line',
      textAlign: 'left'  }
    },
    {
      url: banner2,
      title: "BESPOKE 김치플러스 세일",
      titleStyle: {color: 'black', textAlign: 'left'},
      subtitle: "10만P 보상 혜택에 삼성위크 20만 상당 혜택까지\n올 가을 BESPOKE 김치플러스로 키친 완성", 
      subtitleStyle: { color: 'black', whiteSpace: 'pre-line', textAlign: 'left' }
    },
];

const categories = ['삼닷 페스타 연말 결산', '갤럭시 S23 FE', 'BESPOKE 그랑데 AI',  'BESPOKE 냉장고', '갤럭시 북3 Intel Evo', '삼성닷컴 월간TV', 'Samsung Experience','삼성 컬처랩' ];
const [showSlideshow, setShowSlideshow] = useState(false); // 슬라이드쇼 표시 상태 추가

const handleCategoryClick = (category) => {
  console.log(`You clicked ${category}`);
};

useEffect(() => {
  setShowSlideshow(true); // 컴포넌트가 마운트되면 슬라이드쇼 표시 상태를 true로 변경
}, []);

return (
  <div className="home">
    {showSlideshow && <Slideshow images={images} />} {/* 슬라이드쇼 표시 상태에 따라 슬라이드쇼 컴포넌트 렌더링 */}

    {/* 카테고리 6개 */}
    <section className="category-section">
      {categories.map((category, index) => (
        <div key={index} className='category' onClick={() => handleCategoryClick(category)}>
          {category}
        </div>
      ))}
    </section>

        <div className="image-grid">
      <section >
        <img src={subimg1}/>
        <img src={subimg2}/>
        <img src={subimg3}/>
        <img src={subimg4}/>
      </section>
      <section>
        <img src={subimg5}/>
        <img src={subimg6}/>
        <img src={subimg7}/>
        <img src={subimg8}/>
      </section>
      </div>

      <section className="bigimg">
        <div className="bigimg-content">
          <h1>TV & 오디오</h1>
          <span>Neo QLED 8K</span> <span>삼성 OLED</span> <span>TheSerif</span> <span>JBL턴테이블</span>
          <h2 style={{marginTop:"80%"}}>Neo QLED 8K</h2>
          <p>Neo 퀸텀 매트릭스로 완성하는 압도적 초고화질과</p>
          <p>서라운드 사운드로 영화관 같은 몰입감을 누려보세요</p>
          <button className="purchase-button">구매 혜택 보기</button>
        </div>
        <img src={bigimg1} alt="빅 이미지" />
      </section>

      <section className="bigimg">
        <div className="bigimg-content" style={{color:"black"}}>
          <h1>모바일 & PC</h1>
          <span>갤럭시 시리즈</span> <span>Galaxy Watch6 Series</span> <span>Galaxy Book3</span>
          <h2 style={{marginTop:"80%"}}>Neo QLED 8K</h2>
          <p>Neo 퀸텀 매트릭스로 완성하는 압도적 초고화질과</p>
          <p>서라운드 사운드로 영화관 같은 몰입감을 누려보세요</p>
          <button className="purchase-button" style={{backgroundColor:"black", color:"white"}}>구매 혜택 보기</button>
        </div>
        <img src={bigimg2} alt="빅 이미지" />
      </section>

  </div>
);
}

export default Home;
