import React, { useState, useEffect, useRef } from 'react';
import './Notice.css';
import Quill from 'quill';
import 'quill/dist/quill.snow.css'; // import quill styles

const Notice = () => {
  const [notices, setNotices] = useState([
    { id: 1, title: 'Welcome!', content: 'Welcome to our service!' },
    { id: 2, title: 'Maintenance Notice', content: 'The service will be down for maintenance on September 1st.' }
  ]);
  const [newNotice, setNewNotice] = useState({ title: '', content: '' });
  const quillRef = useRef(null);

  useEffect(() => {
    const quill = new Quill(quillRef.current, {
      theme: 'snow',
      modules: {
        toolbar: [
          [{ 'header': '1'}, { 'header': '2' }],
          [{ 'list': 'ordered'}, { 'list': 'bullet' }],
          ['bold', 'italic', 'underline'],
          ['image', 'video']
        ]
      }
    });

    quill.on('text-change', () => {
      setNewNotice((prev) => ({
        ...prev,
        content: quill.root.innerHTML
      }));
    });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNotice({ ...newNotice, [name]: value });
  };

  const addNotice = (e) => {
    e.preventDefault();
    if (newNotice.title && newNotice.content) {
      setNotices([
        ...notices,
        { id: notices.length + 1, title: newNotice.title, content: newNotice.content }
      ]);
      setNewNotice({ title: '', content: '' });
      quillRef.current.querySelector('.ql-editor').innerHTML = ''; // Clear editor
    }
  };

  const deleteNotice = (id) => {
    setNotices(notices.filter((notice) => notice.id !== id));
  };

  return (
    <div className="container">
      <header className="main-header">
        <h1>공지사항 관리</h1>
      </header>
      <div className="notice-section">
        <section className="notice-form">
          <h4>새 공지사항 추가</h4>
          <form onSubmit={addNotice}>
            <input
              type="text"
              name="title"
              placeholder="공지사항 제목"
              value={newNotice.title}
              onChange={handleInputChange}
              required
            />
            <div ref={quillRef} style={{ height: '200px' }}></div>
            <button type="submit">공지사항 등록</button>
          </form>
        </section>
        <section className="notice-list">
          <h4>공지사항 목록</h4>
          <ul>
            {notices.map((notice) => (
              <li key={notice.id}>
                <h5>{notice.title}</h5>
                <div dangerouslySetInnerHTML={{ __html: notice.content }}></div>
                <button onClick={() => deleteNotice(notice.id)}>삭제</button>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
};

export default Notice;
