import React, { useState, useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import './style.css';

Chart.register(...registerables);

const Admin = () => {
  const [stats, setStats] = useState([]);
  const [members, setMembers] = useState([]);
  const [filteredMembers, setFilteredMembers] = useState([]);
  const [totalMembers, setTotalMembers] = useState(0); // 총 회원 수 상태 추가
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    id: '',
    name: '',
    email: '',
    phone: ''
  });
  const chartRef = useRef(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const membersResponse = await fetch('http://localhost:3001/api/members');
      const statsResponse = await fetch('http://localhost:3001/api/stats');
  
      if (!membersResponse.ok || !statsResponse.ok) {
        throw new Error('HTTP error!');
      }
  
      const membersData = await membersResponse.json();
      const statsData = await statsResponse.json();
  
      setMembers(membersData);
      setFilteredMembers(membersData);
      setStats(statsData);
      setTotalMembers(membersData.length); // 총 회원 수 업데이트
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };
  
  useEffect(() => {
    fetchData();
    const intervalId = setInterval(fetchData, 24 * 60 * 60 * 1000); // 1일마다 업데이트

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    if (chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      if (ctx.chart) {
        ctx.chart.destroy();
      }

      const labels = stats.map(stat => stat.date);
      const dataAdded = stats.map(stat => stat.added);
      const dataRemoved = stats.map(stat => stat.removed);

      const chart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [
            {
              label: 'Added',
              data: dataAdded,
              backgroundColor: '#36A2EB',
              borderColor: '#36A2EB',
              borderWidth: 1
            },
            {
              label: 'Removed',
              data: dataRemoved,
              backgroundColor: '#FF6384',
              borderColor: '#FF6384',
              borderWidth: 1
            }
          ]
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              display: true,
            },
            tooltip: {
              callbacks: {
                label: function(context) {
                  let label = context.dataset.label || '';
                  if (label) {
                    label += ': ';
                  }
                  if (context.parsed.y !== null) {
                    label += new Intl.NumberFormat().format(context.parsed.y);
                  }
                  return label;
                }
              }
            }
          },
          scales: {
            x: {
              beginAtZero: true
            },
            y: {
              beginAtZero: true
            }
          }
        }
      });

      ctx.chart = chart;

      return () => {
        chart.destroy();
      };
    }
  }, [stats]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  useEffect(() => {
    const filtered = members.filter(member => {
      return (
        (filters.id === '' || member.id.includes(filters.id)) &&
        (filters.name === '' || member.name.toLowerCase().includes(filters.name.toLowerCase())) &&
        (filters.email === '' || member.email.toLowerCase().includes(filters.email.toLowerCase())) &&
        (filters.phone === '' || member.phone.includes(filters.phone))
      );
    });
    setFilteredMembers(filtered);
  }, [filters, members]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="admin-container">
      <div className="chart-section">
        <h2>Member Statistics</h2>
        <div className="chart-container">
          <canvas ref={chartRef} id="memberChart"></canvas>
        </div>
      </div>
      <div className="table-section">
        <div className="total-members">
          <h3>Total Members: {totalMembers}</h3> {/* 총 회원 수 표시 */}
        </div>
        <h3>Member List</h3>
        <div className="filters">
          <input
            type="text"
            name="id"
            placeholder="Filter by ID"
            value={filters.id}
            onChange={handleFilterChange}
          />
          <input
            type="text"
            name="name"
            placeholder="Filter by Name"
            value={filters.name}
            onChange={handleFilterChange}
          />
          <input
            type="text"
            name="email"
            placeholder="Filter by Email"
            value={filters.email}
            onChange={handleFilterChange}
          />
          <input
            type="text"
            name="phone"
            placeholder="Filter by Phone"
            value={filters.phone}
            onChange={handleFilterChange}
          />
        </div>
        <div className="table-container">
          <table className="members-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
              </tr>
            </thead>
            <tbody>
              {filteredMembers.map(member => (
                <tr key={member.id}>
                  <td>{member.id}</td>
                  <td>{member.name}</td>
                  <td>{member.email}</td>
                  <td>{member.phone}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Admin;
