import React, { useState } from 'react';
import './style.css';

const intros = [
  {
    id: 1,
    title: 'Introduction 1',
    content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
  },
  {
    id: 2,
    title: 'Introduction 2',
    content: 'Praesent ac velit non magna aliquam aliquet.',
  },
  {
    id: 3,
    title: 'Introduction 3',
    content: 'Etiam convallis enim ut enim varius, et laoreet risus condimentum.',
  },
  {
    id: 4,
    title: 'Introduction 4',
    content: 'Vivamus et sem eu felis tempor vestibulum non a nisl.',
  },
  {
    id: 5,
    title: 'Introduction 5',
    content: 'Nullam scelerisque tortor quis porta fermentum.',
  },
];

function Intros() {
  const [selectedIntro, setSelectedIntro] = useState(null);
  const [extraContent, setExtraContent] = useState('');

  const handleIntroClick = (id) => {
    setSelectedIntro(id);
  };

  const handleExtraContentChange = (event) => {
    setExtraContent(event.target.value);
  };

  return (
    <div className="intros-container">
      {intros.map((intro) => (
        <div
          key={intro.id}
          className={`intro-card ${selectedIntro === intro.id ? 'active' : ''}`}
          onClick={() => handleIntroClick(intro.id)}
        >
          <h2 className="intro-title">{intro.title}</h2>
          <p className="intro-content">{intro.content}</p>
        </div>
      ))}
      <div className="intro-card intro-extra">
        <h2 className="intro-title">성장과정</h2>
        <textarea
          className="intro-content intro-extra-content"
          value={extraContent}
          onChange={handleExtraContentChange}
          maxLength={500}
          placeholder="최대 500자까지 입력할 수 있습니다."
        />
      </div>
    </div>
  );
}

export default Intros;
