import React, { useState, useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import './style.css';

Chart.register(...registerables);

const Admin = () => {
  const [stats, setStats] = useState({ totalMembers: 0 });
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const chartRef = useRef(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [statsResponse, membersResponse] = await Promise.all([
        fetch('http://localhost:3001/api/member-stats'),
        fetch('http://localhost:3001/api/members')
      ]);

      const statsData = await statsResponse.json();
      const membersData = await membersResponse.json();

      setStats({
        totalMembers: statsData.totalMembers
      });
      setMembers(membersData);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const intervalId = setInterval(fetchData, 24 * 60 * 60 * 1000); // 1일마다 업데이트

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    if (chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      if (ctx.chart) {
        ctx.chart.destroy();
      }

      const chart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: ['Total Members'],
          datasets: [{
            label: 'Member Statistics',
            data: [stats.totalMembers],
            backgroundColor: ['#36A2EB'],
            borderColor: ['#36A2EB'],
            borderWidth: 1
          }]
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              display: true,
            },
            tooltip: {
              callbacks: {
                label: function(context) {
                  let label = context.dataset.label || '';
                  if (label) {
                    label += ': ';
                  }
                  if (context.parsed.y !== null) {
                    label += new Intl.NumberFormat().format(context.parsed.y);
                  }
                  return label;
                }
              }
            }
          },
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });

      ctx.chart = chart;

      return () => {
        chart.destroy();
      };
    }
  }, [stats]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="admin-container">
      {/* <div className="chart-section">
        <h2>Member Statistics</h2>
        <div className="chart-container">
          <canvas ref={chartRef} id="memberChart"></canvas>
        </div>
      </div> */}
      <div className="table-section">
        <h3>Member List</h3>
        <table className="members-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
            </tr>
          </thead>
          <tbody>
            {members.map(member => (
              <tr key={member.id}>
                <td>{member.id}</td>
                <td>{member.name}</td>
                <td>{member.email}</td>
                <td>{member.phone}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Admin;
