import React, { useState } from 'react';
import './Notice.css';

const Notice = () => {
  // 공지사항 상태 관리
  const [notices, setNotices] = useState([
    { id: 1, title: '환영합니다!', content: '서비스를 이용해 주셔서 감사합니다!' },
    { id: 2, title: '서버 업데이트 안내', content: '9월 1일에 서버 업데이트가 있을 예정입니다.' }
  ]);
  const [newNotice, setNewNotice] = useState({ title: '', content: '' });

  // 입력값 변경 처리
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNotice({ ...newNotice, [name]: value });
  };

  // 공지사항 추가 처리
  const addNotice = (e) => {
    e.preventDefault();
    if (newNotice.title && newNotice.content) {
      setNotices([
        ...notices,
        { id: notices.length + 1, title: newNotice.title, content: newNotice.content }
      ]);
      setNewNotice({ title: '', content: '' });
    }
  };

  // 공지사항 삭제 처리
  const deleteNotice = (id) => {
    setNotices(notices.filter((notice) => notice.id !== id));
  };

  return (
    <div className="container">
      <header className="main-header">
        <h1>공지사항 관리</h1>
      </header>
      <div className="notice-section">
        <section className="notice-form">
          <h4>새 공지사항 추가</h4>
          <form onSubmit={addNotice}>
            <input
              type="text"
              name="title"
              placeholder="공지사항 제목"
              value={newNotice.title}
              onChange={handleInputChange}
              required
            />
            <textarea
              name="content"
              placeholder="공지사항 내용"
              value={newNotice.content}
              onChange={handleInputChange}
              required
            ></textarea>
            <button type="submit">공지사항 등록</button>
          </form>
        </section>
        <section className="notice-list">
          <h4>공지사항 목록</h4>
          <ul>
            {notices.map((notice) => (
              <li key={notice.id}>
                <h5>{notice.title}</h5>
                <p>{notice.content}</p>
                <button onClick={() => deleteNotice(notice.id)}>삭제</button>
              </li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
};

export default Notice;
