import React, { useState, useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import './style.css';

Chart.register(...registerables);

const Admin = () => {
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const chartRef = useRef(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const membersResponse = await fetch('http://localhost:3001/api/members');
      if (!membersResponse.ok) {
        throw new Error(`HTTP error! status: ${membersResponse.status}`);
      }
      const membersData = await membersResponse.json();
      console.log('Fetched Members Data:', membersData);
      setMembers(membersData);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const intervalId = setInterval(fetchData, 24 * 60 * 60 * 1000); // 1일마다 업데이트

    return () => clearInterval(intervalId);
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="admin-container">
      <div className="table-section">
        <h3>Member List</h3>
        <table className="members-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
            </tr>
          </thead>
          <tbody>
            {members.map(member => (
              <tr key={member.id}>
                <td>{member.id}</td>
                <td>{member.name}</td>
                <td>{member.email}</td>
                <td>{member.phone}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Admin;
