import React, { useState, useEffect } from 'react';
import { TransitionGroup, CSSTransition } from "react-transition-group";
import '../Slideshow/style.css';

const Slideshow = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrev = () => {
    const prevIndex = currentIndex === 0 ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(prevIndex);
  };

  const handleNext = () => {
    const nextIndex = currentIndex === images.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(nextIndex);
  };

  useEffect(() => {
    const timerId = setTimeout(handleNext, 3000);
    // 자동으로넘어가는시간 3초로 설정
    return () => clearTimeout(timerId);
  }, [currentIndex]);

  return (
    <section className="home-image">
      <TransitionGroup>
        <CSSTransition key={currentIndex} timeout={500} classNames="fade">
          <div className='image-container'>
            <img src={images[currentIndex].url} alt='Samsung' />
            <div className='image-title' style={images[currentIndex].titleStyle}>{images[currentIndex].title}</div>
            <div className='image-subtitle' style={images[currentIndex].subtitleStyle}>{images[currentIndex].subtitle}</div>
          </div>
        </CSSTransition>
      </TransitionGroup>

      <button onClick={handlePrev} className='prev-button'>&lt;</button>
      <button onClick={handleNext} className='next-button'>&gt;</button>
    </section>
  );
};

export default Slideshow;
