// front/src/Views/subfolder/admin/index.js

import React, { useState, useEffect } from 'react';
import { Chart, registerables } from 'chart.js';
import './style.css';

Chart.register(...registerables);

const Admin = () => {
  const [data, setData] = useState({ active: 0, inactive: 0 });
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const chartRef = React.useRef(null);

  useEffect(() => {
    // 데이터 가져오기
    const fetchData = async () => {
      try {
        const [statsResponse, membersResponse] = await Promise.all([
          fetch('/api/member-stats'),
          fetch('/api/members')
        ]);

        const statsData = await statsResponse.json();
        const membersData = await membersResponse.json();

        setData({ active: statsData.active, inactive: statsData.inactive });
        setMembers(membersData);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 24 * 60 * 60 * 1000); // 1일마다

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    // 기존 차트 삭제
    if (ctx.chart) {
      ctx.chart.destroy();
    }

    const chart = new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Active Members', 'Inactive Members'],
        datasets: [{
          label: 'Members',
          data: [data.active, data.inactive],
          backgroundColor: ['#36A2EB', '#FF6384'],
          borderColor: ['#36A2EB', '#FF6384'],
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: false, // 범례 숨기기
          },
          tooltip: {
            callbacks: {
              label: function(context) {
                let label = context.dataset.label || '';
                if (label) {
                  label += ': ';
                }
                if (context.parsed.y !== null) {
                  label += new Intl.NumberFormat().format(context.parsed.y);
                }
                return label;
              }
            }
          }
        },
        scales: {
          x: {
            beginAtZero: true,
            grid: {
              display: false
            }
          },
          y: {
            beginAtZero: true,
            grid: {
              borderDash: [5, 5]
            }
          }
        }
      }
    });

    // 차트 인스턴스를 ctx에 저장
    ctx.chart = chart;

    return () => {
      // 컴포넌트 언마운트 시 차트 제거
      chart.destroy();
    };
  }, [data]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="admin-container">
      <h2>Member Statistics</h2>
      <div className="chart-container">
        <canvas ref={chartRef} id="memberChart"></canvas>
      </div>
      <h3>Member List</h3>
      <table className="members-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Joined Date</th>
          </tr>
        </thead>
        <tbody>
          {members.map(member => (
            <tr key={member.id}>
              <td>{member.id}</td>
              <td>{member.name}</td>
              <td>{member.email}</td>
              <td>{new Date(member.joinedDate).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Admin;
